async function createReadingPage(){let e=document.querySelector(".main-page");e.dataset.currentPage="reading-page",e.querySelector(".book-genre-list").remove();let t=e.querySelector(".book-content__header").cloneNode(!0);t.classList.add("book-content__fixed-button"),t.classList.add("change-page-button"),t.dataset.menuLink="current-book-page",e.querySelector(".book-content").prepend(t),Array.from(e.querySelectorAll("button")).forEach((e=>{e.remove()}));let[o,r]=e.querySelector(".book-content").dataset.book.split(","),a=await fetch("https://my-library-project-server.herokuapp.com/reading?book_name="+o+"&book_author="+r);if(a.ok){let t=await a.text();e.querySelector(".book-content__content").innerHTML=t}else alert(a.status);e.querySelector(".comment-block").remove()}