function blockAppear(e,o){return function(){Array.from(document.querySelectorAll(e)).forEach((e=>{document.documentElement.scrollTop+document.documentElement.clientHeight>e.offsetTop+.75*e.offsetHeight&&document.documentElement.scrollTop<e.offsetTop&&e.classList.add(o),(document.documentElement.scrollTop>e.offsetTop+e.offsetHeight||document.documentElement.scrollTop+document.documentElement.clientHeight<e.offsetTop+.75*e.offsetHeight)&&e.classList.remove(o)}))}}window.addEventListener("scroll",blockAppear(".decoration-block","_appeared")),window.addEventListener("scroll",blockAppear(".advertisement-block__picture","_advert-appeared"));